import React, { useState, useEffect } from "react";
import SinglePokemon from "./SinglePokemon";

const Pokemon = () => {
  // Remember state is data that changes overtime, since we're fetching our data from the a database
  // we want to put our data retrieved from our backend/database in an array container
  const [pokemon, setPokemon] = useState([]);

  /* 
  How to write the useEffect hook
  1. import it from react library 
  2. useEffect takes in some arguments the 
        - first is a function we'll be using an arrow function
        - the second one is optional, there are reasons for when you want to include it and there are reasons when not to
  3. Second argument is a dependency list/array - if we leave if blank or place something inside of it is has a certain behavior

  */

  // useEffect without a dependecy array it triggers off on every render
  useEffect(() => {
    console.log("I re-rendered");
  });

  // We need to use useEffect to load our initial data and set our pokemon array in our data to that initial data
  // This occurs on our first render/mount only!
  useEffect(() => {
    const fetchPokemon = async () => {
      const response = await fetch("https://pokeapi.co/api/v2/pokemon");
      console.log(response);
      const data = await response.json();
      console.log("data", data);
      setPokemon(data.results);
    };
    // call fetch pokemon function to get data
    fetchPokemon();
  }, []);
  return <div>Pokemon</div>;
};

export default Pokemon;
